from src.Year2016.Day2 import Day2
from unittest import TestCase


class Day2Test(TestCase):
    def setUp(self):
        self.day = Day2()

    def tearDown(self):
        pass

    def test_simple_pad(self):
        pad = ['123',
               '456',
               '789']
        sequence = '''ULL
RRDDD
LURDL
UUUUD'''
        code = self.day.find_seq(pad, sequence)
        self.assertEqual(code, '1985')

    def test_puzzle(self):
        pad = ['123',
               '456',
               '789']
        code = self.day.find_seq(pad, self.puzzle())
        self.assertEqual(code, '18843')

    def test_simple_case_complex_pad(self):
        pad = '''00100
02340
56789
0ABC0
00D00'''.split('\n')
        sequence = '''ULL
RRDDD
LURDL
UUUUD'''
        code = self.day.find_seq(pad, sequence, position=[2, 0])
        self.assertEqual(code, '5DB3')

    def test_puzzle_complex_pad(self):
        pad = '''00100
02340
56789
0ABC0
00D00'''.split('\n')

        code = self.day.find_seq(pad, self.puzzle(), position=[2, 0])
        self.assertEqual(code, '67BB9')

    def puzzle(self):
        return '''



'''
