from unittest import TestCase


def waddle(tube):
    x, y = (tube.index('|'), 0)
    directions = {
        'south': (0, 1),
        'north': (0, -1),
        'west': (-1, 0),
        'east': (1, 0),
    }
    step = 0
    letters = [c for c in tube if c.isalpha()]
    tube = tube.split('\n')
    path = []
    direction = 'south'
    while len(path) < len(letters):
        if tube[y][x] in letters:
            path.append(tube[y][x])
        if tube[y][x] is '+':
            if direction in ['south', 'north']:
                if 0 < y < len(tube) and 0 < x - 1 < len(tube[y]) and tube[y][x - 1] in ['-'] + letters:
                    direction = 'west'
                else:
                    direction = 'east'
            elif direction in ['west', 'east']:
                if 0 < y + 1 < len(tube) and 0 < x < len(tube[y + 1]) and tube[y + 1][x] in ['|'] + letters:
                    direction = 'south'
                else:
                    direction = 'north'
        x, y = x + directions[direction][0], y + directions[direction][1]
        step += 1
        
    return ''.join(path), step


class Day19Test(TestCase):
    def simple_puzzle(self):
        return '''     |
     |  +--+
     A  |  C
 F---|----E|--+
     |  |  |  D
     +B-+  +--+
'''

    def test_simple_test(self):
        self.assertEqual(waddle(self.simple_puzzle()), ('ABCDEF', 38))

    def test_puzzle(self):
        self.assertEqual(waddle(self.puzzle()), ('LIWQYKMRP', 16764))

